import React, { useState, useEffect } from "react";
import "./AutoScrollGallery.css";

// ایمپورت تصاویر از پوشه src/assets
import soperGif from "../../assets/soper.gif";
import ronixWebp from "../../assets/Ronix.webp";
import zibaiiWebp from "../../assets/zibaii.webp"; // تغییر نام فایل به zibaii.webp

const slidesData = [
  {
    id: 1,
    image: soperGif, // استفاده از ماژول ایمپورت شده
    link: "https://www.digikala.com/product-list/plp_254230691/?&promo_name=%D9%BE%D8%A7%D8%B1%D8%AA%D9%86%D8%B1%D8%B4%DB%8C%D9%BE-202&promo_position=home_slider_new_v2&promo_creative=194229&bCode=194229",
    alt: "تصویر اول",
  },
  {
    id: 2,
    image: ronixWebp,
    link: "https://www.digikala.com/landing/esfand1403-tl/?&promo_name=%D8%A2%D8%AE%D8%B1%DB%8C%D9%86+%D8%AD%D8%B1%D8%A7%D8%AC+%D8%B3%D8%A7%D9%84+%D8%A7%D8%A8%D8%B2%D8%A7%D8%B1+%D9%88+%D8%AA%D8%AC%D9%87%DB%8C%D8%B2%D8%A7%D8%AA+1403&promo_position=home_slider_new_v2&promo_creative=194009&bCode=194009",
    alt: "تصویر دوم",
  },
  {
    id: 3,
    image: zibaiiWebp,
    link: "https://www.digikala.com/landing/endofyear/?&promo_name=%D9%BE%D8%A7%D8%B1%D8%AA%D9%86%D8%B1%D8%B4%DB%8C%D9%BE-%D9%87%D9%86%DA%A9%D9%84&promo_position=home_slider_new_v2&promo_creative=194233&bCode=194233",
    alt: "تصویر سوم",
  },
];

const AutoScrollGallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // هر 3 ثانیه به اسلاید بعدی برود
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  // تابع اسلاید بعدی
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % slidesData.length);
  };

  // تابع اسلاید قبلی
  const prevSlide = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + slidesData.length) % slidesData.length
    );
  };

  return (
    <div className="carousel-container">
      {slidesData.map((slide, index) => (
        <div
          className={`carousel-slide ${index === currentIndex ? "active" : ""}`}
          key={slide.id}
        >
          <a href={slide.link}>
            <img src={slide.image} alt={slide.alt} />
          </a>
        </div>
      ))}

      <button className="prev-btn" onClick={prevSlide}>
        &#8249;
      </button>
      <button className="next-btn" onClick={nextSlide}>
        &#8250;
      </button>
    </div>
  );
};

export default AutoScrollGallery;
