import React from 'react';
import './SuperOfferBanner.css';

function SuperOfferBanner() {
  return (
    <div className="banner">
      <div className="banner-content">
        <button className="discount-button">
          SuperOffer<br />
          ۵۰٪ تخفیف
        </button>
        <div className="product-images">
          <img src="/images/product1.jpg" alt="محصول 1" className="product" />
          <img src="/images/product2.jpg" alt="محصول 2" className="product" />
          <img src="/images/product3.jpg" alt="محصول 3" className="product" />
          <img src="/images/product4.jpg" alt="محصول 4" className="product" />
          <img src="/images/product5.jpg" alt="محصول 5" className="product" />
        </div>
        <div className="shopping-cart">
          <img src="/images/cart.png" alt="سبد خرید" />
        </div>
      </div>
    </div>
  );
}

export default SuperOfferBanner;
