import React from 'react';
import './PromoBanners.css';
import brandLinks from './brandLinks'; // ایمپورت لینک‌ها

const LogosBanner = () => {
  return (
    <div className="logos-banner">
      <div className="header">
        <span className="header-text">محبوب‌ترین برندها</span>
        <span className="star-icon" />
      </div>
      <div className="logos-container">
        {brandLinks.map((brand, index) => (
          <a key={index} href={brand.url} target="_blank" rel="noopener noreferrer" className="logo-link">
            <img src={brand.logo} alt={brand.name} />
          </a>
        ))}
      </div>
    </div>
  );
};

const PromoBanners = () => {
  return (
    <div className="promo-banners-container">
      <LogosBanner />
    </div>
  );
};

export default PromoBanners;
