import React from "react";
import "./Article.css";

// اگر خواستید تصاویر را وارد کنید، به‌صورت زیر عمل کنید:
// import logoImg from "./assets/logo.png";
// import icon1Img from "./assets/icon1.png";
// import icon2Img from "./assets/icon2.png";
// import icon3Img from "./assets/icon3.png";
// import icon4Img from "./assets/icon4.png";

const Article = () => {
  return (
    <div className="article-container">
      {/* نوار بالایی (لوگو) */}
      <div className="top-bar">
        <div className="logo-container">
          {/* جایگذاری تصویر لوگو */}
          <img src="IMAGE_PATH" alt="لوگو" className="logo-img" />
        </div>
      </div>

      {/* اطلاعات پشتیبانی (شماره تلفن‌ها و توضیحات) */}
      <div className="support-info">
        <span className="support-text">تلفن پشتیبانی ۶۱۹۳۰۰۰۰ - ۰۲۱</span>
        <span className="separator">|</span>
        <span className="support-text">۰۲۱-۹۱۰۰۰۱۰۰</span>
        <span className="separator">|</span>
        <span className="support-text">۷ روز هفته، ۲۴ ساعته پاسخگوی شما هستیم</span>
      </div>

      {/* بخش آیکن‌ها یا آیتم‌های زیر */}
      <div className="icon-section">
        <div className="icon-item">
          {/* آیکن یا تصویر اول */}
          <img src="IMAGE_PATH" alt="icon1" className="icon-img" />
          <span>  اﻣﮑﺎن ﺗﺤﻮﯾﻞ اﮐﺴﭙﺮس</span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر دوم */}
          <img src="IMAGE_PATH" alt="icon2" className="icon-img" />
          <span> امکان پرداخت در محل</span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر سوم */}
          <img src="IMAGE_PATH" alt="icon3" className="icon-img" />
          <span>هفت روز ضمانت بازگشت کالا  </span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر چهارم */}
          <img src="IMAGE_PATH" alt="icon4" className="icon-img" />
          <span>صفحات  برای شما</span>
        </div>
      </div>
    </div>
  );
};

export default Article;
