/**
 * کامپوننت: NewHorizontalMenu
 *
 * نقشه راه پیاده‌سازی:
 * 1) تعریف آرایه‌ای از آیتم‌ها (شامل آیکون یا عکس، عنوان، لینک اختیاری).
 * 2) ایجاد ساختار HTML با یک کانتینر افقی که آیتم‌ها را کنار هم نمایش دهد.
 * 3) تنظیم استایل برای نمایش آیتم‌ها در یک ردیف اسکرولی.
 * 4) استفاده از عکس‌های موقت تا زمانی که عکس‌های اصلی آماده شوند.
 * 5) دقت به جزئیات ظاهری مثل فاصله‌ها، گرد بودن عکس‌ها و متن زیر آیکون‌ها.
 */

import React from 'react';
import './Table.css';

// آرایه‌ای از آیتم‌ها. در اینجا از آدرس عکس موقت استفاده شده.
const menuItems = [
  { id: 1, icon: 'placeholder1.png', title: 'بیشتر' },
  { id: 2, icon: 'placeholder2.png', title: 'تخفیف بومی '},
  { id: 3, icon: 'placeholder3.png', title: 'حراج فروشگاه حضوری' },
  { id: 4, icon: 'placeholder4.png', title: 'دی جی کالا سرویس' },
  { id: 5, icon: 'placeholder5.png', title: 'اشتراک پلاس' },
  { id: 6, icon: 'placeholder6.png', title: 'تخفیف برندها' },
  { id: 7, icon: 'placeholder7.png', title: '۴قسط' },
  { id: 8, icon: 'placeholder8.png', title: 'پس نده ' },
  { id: 9, icon: 'placeholder9.png', title: 'خرید طلای دیجیتال' },
  { id: 10, icon: 'placeholder10.png', title: 'حراج موبایل' },
  { id: 11, icon: 'placeholder11.png', title: 'جعبه‌باز' },
];

const Table = () => {
  return (
    <div className="menu-container">
      {menuItems.map((item) => (
        <div key={item.id} className="menu-item">
          <div className="menu-icon">
            {/* وقتی عکس اصلی را داشتی، به جای placeholder از لینک اصلی استفاده کن */}
            <img src={item.icon} alt={item.title} />
          </div>
          <p>{item.title}</p>
        </div>
      ))}
    </div>
  );
};

export default Table;
