// مسیر فایل: src/components/LocationSelector/LocationSelector.js
import { useState } from "react";
import { FaMapMarkerAlt } from "react-icons/fa";
import "./LocationSelector.css";

const LocationSelector = () => {
  const [location, setLocation] = useState("سمنان");
  const [isOpen, setIsOpen] = useState(false);
  const locations = ["تهران", "مشهد", "اصفهان", "شیراز", "تبریز", "کرمان"];

  return (
    <div className="location-selector">
      <button className="location-button" onClick={() => setIsOpen(!isOpen)}>
        <FaMapMarkerAlt /> ارسال به {location}
      </button>
      {isOpen && (
        <ul className="location-dropdown">
          {locations.map((loc) => (
            <li key={loc} onClick={() => { setLocation(loc); setIsOpen(false); }}>
              {loc}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default LocationSelector;
