import React from "react";
import "./Article.css";

// اگر خواستید تصاویر را وارد کنید، به‌صورت زیر عمل کنید:
// import logoImg from "./assets/logo.png";
// import icon1Img from "./assets/icon1.png";
// import icon2Img from "./assets/icon2.png";
// ...

const Article = () => {
  return (
    <div className="article-container">
      {/* نوار بالایی (لوگو و شماره تماس) */}
      <div className="top-bar">
        <div className="logo-container">
          {/* جایگذاری تصویر لوگو */}
          <img src="IMAGE_PATH" alt="لوگو" className="logo-img" />
        </div>
        <div className="phone-number">
          <span>۰۲۱ - ۶۱۹۳۰۰۰۰</span>
        </div>
      </div>

      {/* بخش آیکن‌ها یا آیتم‌های زیر */}
      <div className="icon-section">
        <div className="icon-item">
          {/* آیکن یا تصویر اول */}
          <img src="IMAGE_PATH" alt="icon1" className="icon-img" />
          <span>پرفروش ترین ها</span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر دوم */}
          <img src="IMAGE_PATH" alt="icon2" className="icon-img" />
          <span>جدیدترین ها</span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر سوم */}
          <img src="IMAGE_PATH" alt="icon3" className="icon-img" />
          <span>محبوب ترین ها</span>
        </div>
        <div className="icon-item">
          {/* آیکن یا تصویر چهارم */}
          <img src="IMAGE_PATH" alt="icon4" className="icon-img" />
          <span>صفحات پیشنهادی برای شما</span>
        </div>
      </div>
    </div>
  );
};

export default Article;
