import React from "react";
import "./HotProducts.css";

// ایمپورت تصاویر (در حال حاضر فقط به عنوان نمونه نوشته شده)
import img1 from "../../assets/1.webp";
import img2 from "../../assets/1.webp";
import img3 from "../../assets/1.webp";

const hotProducts = [
  { id: 1, title: "هندزفری ایکس او مدل EP29 TYPE-C", image: img1 },
  { id: 2, title: "اسپیکر بلوتوثی قابل حمل تسکو مدل TS 23030", image: img2 },
  { id: 3, title: "ماشین لباسشویی نیمه اتوماتیک ریتون مدل RWM-1402 T", image: img3 },
  { id: 4, title: "کاغذ رنگی A4 افق مدل رنگ و وارنگ بسته 15 عددی", image: "عکس" },
  { id: 5, title: "قرص مولتی دیلی دیلی گلوک بسته 30 عددی", image: "عکس" },
  { id: 6, title: "سرویس روتختی مدل چهره های مینیمال 5009 یک نفره 5 تکه", image: "عکس" },
  { id: 7, title: "آچار فرانسه رونیکس مدل RH-2403 سایز 10 اینچ", image: "عکس" },
  { id: 8, title: "بخور سرد کویین مدل QH-760 بسته", image: "عکس" },
  { id: 9, title: "اردو خوری مدل خوشید", image: "عکس" },
  { id: 10, title: "نگهدارنده عینک خودرو کد CJ_147", image: "عکس" },
  { id: 11, title: "سرخ کن بدون روغن سوناشی SAF-900", image: "عکس" },
  { id: 12, title: "چراغ پیشانی اسمال سان مدل ZY-BT006", image: "عکس" },
];

const HotProducts = () => {
  return (
    <div className="hot-products-container">
      <h2 className="hot-title">🔥 داغ ترین چند ساعت گذشته</h2>
      <div className="hot-products-grid">
        {hotProducts.map((product, index) => (
          <div key={product.id} className="hot-product-card">
            <span className="product-rank">{index + 1}</span>
            <img
              src={typeof product.image === "string" ? "" : product.image}
              alt={product.title}
              className="hot-product-image"
            />
            <p className="hot-product-title">{product.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HotProducts;
