import React from "react";

const SupermarketOffers = () => {
  return (
    <div className="flex items-center justify-center bg-gray-100 p-4 rounded-lg shadow-md w-full max-w-6xl mx-auto">
      {/* ✅ بخش راست: عنوان و دکمه */}
      <div className="flex items-center gap-3">
        <img src="/images/shopping-basket.png" alt="سبد خرید تخفیف" className="w-14 h-14" />
        <h2 className="text-green-700 text-lg font-bold">شگفت‌انگیز سوپرمارکتی</h2>
        <button className="bg-green-600 text-white px-3 py-1 rounded-lg text-sm hover:bg-green-700">
          تا ۴۵٪ تخفیف
        </button>
      </div>
      
      {/* ✅ بخش اسلایدر محصولات */}
      <div className="flex gap-2 overflow-x-auto p-2 w-full">
        {[1, 2, 3, 4, 5, 6].map((item) => (
          <div key={item} className="w-20 h-20 bg-white rounded-full flex items-center justify-center shadow-md">
            <img src={`/images/product-${item}.png`} alt={`محصول ${item}`} className="w-16 h-16 rounded-full" />
            <span className="absolute bottom-0 bg-red-600 text-white text-xs px-2 py-1 rounded-full">
              ٪۴۵
            </span>
          </div>
        ))}
      </div>
      
      {/* ✅ بخش دکمه بیشتر */}
      <button className="flex items-center gap-2 text-green-600 hover:text-green-700 font-medium">
        <span>بیش از 100 کالا</span>
        <span className="text-xl">←</span>
      </button>
    </div>
  );
};

export default SupermarketOffers;
