// FooterSection.js
import React from 'react';
import styles from './FooterSection.module.css';
import enamadLogo from '../../components/FooterSection/IMAGES/logo.png'; // تصویر لوگوی Enamad
import samandehiLogo from './assets/samandehiLogo.png'; // تصویر لوگوی Samandehi
import etehadieh from './assets/etehadieh.png'; // تصویر لوگوی اتحادیه کسب‌وکارهای مجازی

// کامپوننت لوگوی Enamad
const EnamadBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.enamadLogo}>
      <img src={enamadLogo} alt="Enamad Logo" />
    </div>
  </div>
);

// کامپوننت لوگوی Samandehi
const SamandehiBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.samandehiLogo}>
      <img src={samandehiLogo} alt="Samandehi Logo" />
    </div>
  </div>
);

// کامپوننت لوگوی اتحادیه کسب‌وکارهای مجازی
const VirtualBusinessUnionBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.virtualBusinessLogo}>
      <img src={etehadieh} alt="Virtual Business Union Logo" />
    </div>
  </div>
);

// کامپوننت اصلی
const FooterSection = () => {
  return (
    <div className={styles.footerSection}>
      <div className={styles.certificationsWrapper}>
        <EnamadBadge />
        <SamandehiBadge />
        <VirtualBusinessUnionBadge />
      </div>
    </div>
  );
};

export default FooterSection;
