// 1️⃣ ساخت کامپوننت اسلایدر تبلیغات
// مسیر: src/components/PromoSlider.js
import React from "react";
import "./PromoSlider.css"; // 2️⃣ استایل‌دهی جداگانه

import tekani from "../../assets/خونه تکونی.webp";


// 3️⃣ آرایه‌ای از تبلیغات برای نمایش داینامیک
const promoData = [
  {
    id: 1,
    title: "خونه تکونی خوش‌شانسی میاره!",
    image: "https://via.placeholder.com/300", // تصویر تستی
    link: "https://example.com/cleaning",
    bgColor: "#3CB5E6",
  },
  {
    id: 2,
    title: "لوازم آماده‌سازی شام آخر سال",
    image: "https://via.placeholder.com/300",
    link: "https://example.com/dinner",
    bgColor: "#F8E0A3",
  },
  {
    id: 3,
    title: "عیدی پلاس رو بگیر!",
    image: "https://via.placeholder.com/300",
    link: "https://example.com/gift",
    bgColor: "#871C7A",
  },
  {
    id: 4,
    title: "محصولاتی برای داشتن پوست و موی سالم",
    image: "https://via.placeholder.com/300",
    link: "https://example.com/skincare",
    bgColor: "#BDE0FE",
  },
];

// 4️⃣ کامپوننت اصلی که تبلیغات رو نمایش می‌ده
const PromoSlider = () => {
  return (
    <div className="promo-wrapper">
    <div className="promo-slider">
      {promoData.map((item) => (
        <a
          key={item.id}
          href={item.link}
          target="_blank"
          rel="noopener noreferrer"
          className="promo-item"
          style={{ backgroundColor: item.bgColor }}
        >
          <img src={item.image} alt={item.title} />
          <p>{item.title}</p>
        </a>
      ))}
    </div>
    </div>
  );
};

export default PromoSlider;

