import React from 'react';
import './ProductList.css';

// تصویر پیش‌فرض را ایمپورت کنید و بعداً با تصاویر خود جایگزین کنید
import train from "../"
const ProductList = () => {
  const products = [
    { id: 'CJ-147', price: '50,000', description: 'تخفیف 15%', icon: '🔥', image: placeholderImage, extraText: 'راهنمای جامع خرید بهترین پسته' },
    { id: 'RH-2403', price: '10,000', description: 'هدیه', icon: '🎁', image: placeholderImage, extraText: 'راهنمای واحد پول کشورهای مختلف برای مسافران ایرانی' },
    { id: 'Y', price: '15,000', description: 'گارانتی', icon: '🔧', image: placeholderImage, extraText: 'فهرست کامل وسایل مورد نیاز سفر با قطار' },
    { id: 'NEW', price: '20,000', description: 'پیشنهاد ویژه', icon: '🌱', image: placeholderImage, extraText: 'آغاز بهار بهترین زمان برای کاشت چه گیاهانی است؟' }
  ];

  return (
    <div className="product-list">
      <h1 className="title">🌟✨ پیشنهادات ویژه امروز ✨🌟</h1>
      <div className="product-grid">
        {products.map((product, index) => (
          <div key={index} className="product-card">
            <img src={product.image} alt={`عکس محصول ${product.id}`} className="product-image" />
            <div className="product-info">
              <p className="product-id">💻 {product.id}</p>
              <p className="product-price">💰 {product.price}</p>
              <p className="product-description">{product.icon} {product.description}</p>
              <p className="product-extra-text">📌 {product.extraText}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
