import React from 'react';
import './PromoBanners.css';

// ایمپورت لوگوهای شرکت‌ها
import euroVitalLogo from '../../assets/EU.jpg';
import chini from '../../assets/chinizarim.png';
import xVisionLogo from '../../assets/xvision.jpg';
import charmmashhad from '../../assets/charmmashhad.jpg';
import huaweiLogo from '../../assets/huawi.png';
import parsKhazarLogo from '../../assets/peril.png';
import parsisLogo from '../../assets/persil.png';

const LogosBanner = () => {
  return (
    <div className="logos-banner">
      <div className="header">
        <span className="star-icon" />
        <span className="header-text">محبوب ترین پرینتر شما</span>
      </div>
      <div className="logos-container">
      <img src={chini} alt="chini" />
        <img src={euroVitalLogo} alt="Euro Vital" />
        <img src={xVisionLogo} alt="X-Vision" />
        <img src={charmmashhad} alt="Garinan" />
        <img src={huaweiLogo} alt="Huawei" />
        <img src={parsKhazarLogo} alt="Pars Khazar" />
        <img src={parsisLogo} alt="Parsis" />
      </div>
    </div>
  );
};

const PromoBanners = () => {
  return (
    <div className="promo-banners-container">
      <LogosBanner />
    </div>
  );
};

export default PromoBanners;
