import React from 'react';
import './OfferImages.css';

// وارد کردن تصاویر با نام‌های جدید
import offer70 from '../../assets/70درصدoffer.gif';
import offer20 from '../../assets/qahveh.webp';

const OfferImages = () => {
  return (
    <div className="offer-container">
      <div className="offer-box">
        <img src={offer70} alt="تخفیف ۷۰ درصد" className="offer-image" />
      </div>
      <div className="offer-box">
        <img src={offer20} alt="تخفیف ۲۰ درصد" className="offer-image" />
      </div>
    </div>
  );
};

export default OfferImages;
