/**
 * کامپوننت: AutoScrollGallery
 *
 * نقشه راه پیاده‌سازی:
 * 1) تعریف آرایه‌ای از اسلایدها شامل عکس، لینک، و متن alt.
 * 2) ذخیره شاخص (ایندکس) اسلاید فعلی در state کامپوننت.
 * 3) استفاده از useEffect برای ایجاد تایمر جهت اسکرول خودکار اسلایدها.
 * 4) پیاده‌سازی توابع nextSlide و prevSlide برای دکمه‌های جلو و عقب.
 * 5) نمایش هر اسلاید و لینک‌دهی آن. فقط اسلاید فعلی نمایش داده شود.
 * 6) نوشتن استایل‌ها در فایل مجزا (AutoScrollGallery.css) جهت کنترل ظاهر.
 */

import React, { useState, useEffect } from "react";
import "./AutoScrollGallery.css";

const slidesData = [
  {
    id: 1,
    image: "../assets/soper.gif", // مسیر عکس اول
    link: "/link1", // لینک صفحه مقصد
    alt: "تصویر اول",
  },
  {
    id: 2,
    image: "../assets/Ronix.webp", // مسیر عکس دوم
    link: "/link2",
    alt: "تصویر دوم",
  },
  {
    id: 3,
    image: "./assets/zibaii.webp", // مسیر عکس سوم
    link: "/link3",
    alt: "تصویر سوم",
  },
];

const AutoScrollGallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // هر 3 ثانیه به اسلاید بعدی برود
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  // تابع اسلاید بعدی
  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % slidesData.length);
  };

  // تابع اسلاید قبلی
  const prevSlide = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + slidesData.length) % slidesData.length
    );
  };

  return (
    <div className="carousel-container">
      {slidesData.map((slide, index) => (
        <div
          className={`carousel-slide ${index === currentIndex ? "active" : ""}`}
          key={slide.id}
        >
          <a href={slide.link}>
            <img src={slide.image} alt={slide.alt} />
          </a>
        </div>
      ))}

      <button className="prev-btn" onClick={prevSlide}>
        &#8249;
      </button>
      <button className="next-btn" onClick={nextSlide}>
        &#8250;
      </button>
    </div>
  );
};

export default AutoScrollGallery;
