// FooterSection.js
import React from "react";
import styles from "./FooterSection.module.css";
import enamadLogo from "../../components/FooterSection/IMAGES/logo.png"; // تصویر لوگوی Enamad
import samandehiLogo from "../../components/FooterSection/IMAGES/logo.png"; // تصویر لوگوی Samandehi
import etehadieh from "../../components/FooterSection/IMAGES/logo.png"; // تصویر لوگوی اتحادیه کسب‌وکارهای مجازی

// کامپوننت لوگوی Enamad
const EnamadBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.enamadLogo}>
      <img src={enamadLogo} alt="Enamad Logo" />
    </div>
  </div>
);

// کامپوننت لوگوی Samandehi
const SamandehiBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.samandehiLogo}>
      <img src={samandehiLogo} alt="Samandehi Logo" />
    </div>
  </div>
);

// کامپوننت لوگوی اتحادیه کسب‌وکارهای مجازی
const VirtualBusinessUnionBadge = () => (
  <div className={styles.badgeContainer}>
    <div className={styles.virtualBusinessLogo}>
      <img src={etehadieh} alt="Virtual Business Union Logo" />
    </div>
  </div>
);

// کامپوننت بلوک متن
const FooterTextBlock = () => (
  <div className={styles.footerTextBlock}>
    <h2 className={styles.footerTitle}>دیجی کالا بیزینس: فوتر این سایت</h2>
    <p className={styles.footerDescription}>
      دیجی‌کالا به عنوان یکی از قدیمی‌ترین فروشگاه‌های اینترنتی با بیش از یک دهه
      تجربه، با پایبندی به سه اصل، پرداخت در محل، ۷ روز ضمانت بازگشت کالا و
      تضمین اصل‌بودن کالا موفق شده تا همگام با فروشگاه‌های معتبر جهان، به
      بزرگ‌ترین فروشگاه اینترنتی ایران تبدیل شود. به محض ورود به سایت دیجی‌کالا
      با دنیایی از کالا رو به رو می‌شوید! هر آنچه که نیاز دارید و به ذهن شما
      خطور می‌کند در اینجا پیدا خواهید کرد. این فروشگاه مثل یک ویترین پر زرق و
      برق است که با انواع و اقسام کالاهایی نظیر گوشی موبایل، ساعت هوشمند،
      تلویزیون، لپ‌تاپ و ... شما را به خود جذب می‌کند.
    </p>
    <a href="#" className={styles.seeMoreLink}>
      مشاهده بیشتر &gt;
    </a>
  </div>
);

// کامپوننت اصلی
const FooterSection = () => {
  return (
    <div className={styles.footerSection}>
      <div className={styles.certificationsWrapper}>
        <EnamadBadge />
        <SamandehiBadge />
        <VirtualBusinessUnionBadge />
      </div>
    </div>
  );
};

export default FooterSection;
