import React from 'react';
import './PromoBanners.css';

const SkincareBanner = () => {
  return (
    <div className="banner skincare-banner">
      {/* لوگو */}
      <div className="logo">
        <img src="https://via.placeholder.com/20" alt="SYN SKIN icon" />
        {/* جایگزین کنید با مسیر عکس لوگو */}
        <span>SYN SKIN</span>
      </div>

      {/* محصولات */}
      <div className="products">
        <img src="https://via.placeholder.com/80x120" alt="Product 1" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 1 */}
        <img src="https://via.placeholder.com/80x120" alt="Product 2" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 2 */}
        <img src="https://via.placeholder.com/80x120" alt="Product 3" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 3 */}
      </div>

      {/* متن */}
      <div className="text">
        <p className="discount">تخفیف 45%</p>
        <p className="summer-products">محصولات تابستانی</p>
        <p className="details">سن سیف و سیف رز تابستانه</p>
        <div className="leaf-icon" />
        {/* جایگزین کنید با مسیر عکس آیکن برگ در CSS */}
      </div>

      {/* دکمه */}
      <button className="buy-button">خرید</button>
    </div>
  );
};

const JellyBanner = () => {
  return (
    <div className="banner jelly-banner">
      {/* المان‌های تزئینی */}
      <div className="decorative-elements">
        <div className="sun" />
        {/* جایگزین کنید با مسیر عکس خورشید در CSS */}
        <div className="wave" />
        {/* جایگزین کنید با مسیر عکس موج در CSS */}
        <div className="beach-ball" />
        {/* جایگزین کنید با مسیر عکس توپ ساحلی در CSS */}
        {/* حباب‌ها رو می‌تونید با CSS یا عکس اضافه کنید */}
      </div>

      {/* محصولات */}
      <div className="products">
        <img src="https://via.placeholder.com/80x120" alt="Jelly Product 1" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 1 */}
        <img src="https://via.placeholder.com/80x120" alt="Jelly Product 2" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 2 */}
        <img src="https://via.placeholder.com/80x120" alt="Jelly Product 3" className="product" />
        {/* جایگزین کنید با مسیر عکس محصول 3 */}
      </div>

      {/* متن */}
      <div className="text">
        <p className="discount">تخفیف 20%</p>
        <p className="summer-products">یه عالمه ژله با طعم سیب، پرتقال، توت فرنگی، سیف رز تابستانه</p>
      </div>

      {/* دکمه */}
      <button className="buy-button">خرید</button>
    </div>
  );
};

const LogosBanner = () => {
  return (
    <div className="logos-banner">
      {/* هدر */}
      <div className="header">
        <span className="star-icon" />
        {/* جایگزین کنید با مسیر عکس ستاره در CSS */}
        <span className="header-text">محسن پرینتر شما</span>
      </div>

      {/* لوگوها */}
      <div className="logos-container">
        <img src="https://via.placeholder.com/120x50" alt="Euro Vital" />
        {/* جایگزین کنید با مسیر لوگوی Euro Vital */}
        <img src="https://via.placeholder.com/80x50" alt="Peyman" />
        {/* جایگزین کنید با مسیر لوگوی Peyman */}
        <img src="https://via.placeholder.com/100x50" alt="X-Vision" />
        {/* جایگزین کنید با مسیر لوگوی X-Vision */}
        <img src="https://via.placeholder.com/50x50" alt="Garinan" />
        {/* جایگزین کنید با مسیر لوگوی Garinan */}
        <img src="https://via.placeholder.com/120x50" alt="Huawei" />
        {/* جایگزین کنید با مسیر لوگوی Huawei */}
        <img src="https://via.placeholder.com/80x50" alt="Pars Khazar" />
        {/* جایگزین کنید با مسیر لوگوی Pars Khazar */}
        <img src="https://via.placeholder.com/50x50" alt="Parsis" />
        {/* جایگزین کنید با مسیر لوگوی Parsis */}
      </div>
    </div>
  );
};

const PromoBanners = () => {
  return (
    <div className="promo-banners-container">
      <div className="banners-container">
        <SkincareBanner />
        <JellyBanner />
      </div>
      <LogosBanner />
    </div>
  );
};

export default PromoBanners;
