// AutoScrollGallery.js
import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/autoplay';
import SwiperCore, { Autoplay } from 'swiper';

// فعال‌سازی ماژول Autoplay
SwiperCore.use([Autoplay]);

const slidesData = [
  {
    id: 1,
    image: require('../../assets/soper.gif'), // مسیر عکس اول
    link: 'https://www.digikala.com/product-list/plp_254230691/',
    alt: 'تصویر اول',
  },
  {
    id: 2,
    image: require('../../assets/Ronix.webp'), // مسیر عکس دوم
    link: 'https://www.digikala.com/landing/esfand1403-tl/',
    alt: 'تصویر دوم',
  },
  {
    id: 3,
    image: require('../../assets/bache.webp'), // مسیر عکس سوم
    link: 'https://www.digikala.com/landing/endofyear/',
    alt: 'تصویر سوم',
  },
];

const AutoScrollGallery = () => {
  return (
    <Swiper
      spaceBetween={50}
      slidesPerView={1}
      autoplay={{ delay: 3000 }}
      loop={true}
    >
      {slidesData.map((slide) => (
        <SwiperSlide key={slide.id}>
          <a href={slide.link}>
            <img src={slide.image} alt={slide.alt} />
          </a>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default AutoScrollGallery;
