const SupermarketOffers = () => {
  const products = [
    { id: 1, img: "/images/product-1.png", discount: "40%" },
    { id: 2, img: "/images/product-2.png", discount: "40%" },
    { id: 3, img: "/images/product-3.png", discount: "40%" },
    { id: 4, img: "/images/product-4.png", discount: "40%" },
    { id: 5, img: "/images/product-5.png", discount: "40%" },
    { id: 6, img: "/images/product-6.png", discount: "40%" },
  ];

  return (
    <div className="flex items-center bg-gray-100 p-4 rounded-lg shadow-md w-full max-w-screen-xl mx-auto">
      {/* بخش دکمه سمت راست */}
      <div className="flex items-center bg-white p-4 rounded-lg shadow-md mr-4">
        <span className="text-green-600 text-lg font-semibold">بیش از 100 کالا</span>
      </div>
      
      {/* اسلایدر محصولات */}
      <div className="flex overflow-x-auto gap-4 flex-1 px-4">
        {products.map((product) => (
          <div key={product.id} className="relative w-24 h-24 bg-white rounded-full shadow-md flex-shrink-0 flex items-center justify-center">
            <img 
              src={product.img} 
              alt={`محصول ${product.id}`} 
              className="w-20 h-20 object-cover rounded-full" 
            />
            <span className="absolute bottom-0 right-0 bg-red-500 text-white text-xs px-2 py-1 rounded-full">
              {product.discount}
            </span>
          </div>
        ))}
      </div>
      
      {/* دکمه تخفیف */}
      <div className="flex items-center bg-green-600 text-white px-4 py-2 rounded-lg ml-4">
        <span className="text-lg font-semibold">تخفیف تا 45%</span>
      </div>
    </div>
  );
};

export default SupermarketOffers;
