  const products = [
    { id: 'CJ-147', price: '50,000', description: 'ÿ™ÿÆŸÅ€åŸÅ 15%', icon: 'üî•', image: placeholderImage },
    { id: 'RH-2403', price: '10,000', description: 'ŸáÿØ€åŸá', icon: 'üéÅ', image: placeholderImage },
    { id: 'Y', price: '15,000', description: '⁄Øÿßÿ±ÿßŸÜÿ™€å', icon: 'üîß', image: placeholderImage },
  ];

  return (
    <div className="product-list">
      <h1 className="title">üåü‚ú® Ÿæ€åÿ¥ŸÜŸáÿßÿØÿßÿ™ Ÿà€å⁄òŸá ÿßŸÖÿ±Ÿàÿ≤ ‚ú®üåü</h1>
      <div className="product-grid">
        {products.map((product, index) => (
          <div key={index} className="product-card">
            <img src={product.image} alt={`ÿπ⁄©ÿ≥ ŸÖÿ≠ÿµŸàŸÑ ${product.id}`} className="product-image" />
            <div className="product-info">
              <p className="product-id">üíª {product.id}</p>
              <p className="product-price">üí∞ {product.price}</p>
              <p className="product-description">{product.icon} {product.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
