// مسیر فایل: src/components/MainMenu/MainMenu.js
import "./MainMenu.css";
import { FaBars, FaShoppingCart, FaFire, FaMobileAlt } from "react-icons/fa";

const MainMenu = () => {
  return (
    <div className="main-menu">
      <button className="menu-button"><FaBars /> دسته‌بندی کالاها</button>
      <nav>
        <ul>
          <li><FaFire /> پرفروش‌ترین‌ها</li>
          <li><FaMobileAlt /> کالاهای دیجیتال</li>
          <li><FaShoppingCart /> سوپرمارکت</li>
        </ul>
      </nav>
    </div>
  );
};

export default MainMenu;
