import React from "react";
import "./TableOne.css";
import fire_power from "../../components/Table_1/images/fire_power.webp";
import star from "../../components/Table_1/images/star.webp";
import TNT from "../../components/Table_1/images/TNT.webp";
import ST from "../../components/Table_1/images/ST.webp";
import vazlin from "../../components/Table_1/images/vazlin.webp";
import vitamin30 from "../../components/Table_1/images/vitamin30.webp";
import sevda from "../../components/Table_1/images/sevda.webp";
import spray from "../../components/Table_1/images/spray.webp";
import vax from "../../components/Table_1/images/vax.webp";
import goshvareh from "../../components/Table_1/images/goshvareh.jpg";
import dastband from "../../components/Table_1/images/dastband.webp";

const TableOne = () => {
  const categories = [
    {
      title: "کرم و مرطوب‌کننده",
      products: [
        { imageUrl: ST, name: "کرم مرطوب کننده درخت چای اس تی ایوز", link: "https://example.com/product1" },
        { imageUrl: vazlin, name: "وازلین لفو", link: "https://example.com/product2" },
        { imageUrl: vitamin30, name: "ویتامین C", link: "https://example.com/product3" },
        { imageUrl: sevda, name: "ژل آبرسان سودا", link: "https://example.com/product4" },
      ],
    },
    {
      title: "موبر",
      products: [
        { imageUrl: spray, name: "اسپری موبر بدن اسکلاره", link: "https://example.com/product5" },
        { imageUrl: vax, name: "وکس موبر کوئین", link: "https://example.com/product6" },
      ],
    },
    {
      title: "انرژی‌زا و نوشیدنی",
      products: [
        { imageUrl: fire_power, name: "Fire Power", link: "https://example.com/product7" },
        { imageUrl: star, name: "Star", link: "https://example.com/product8" },
        { imageUrl: TNT, name: "TNT", link: "https://example.com/product9" },
      ],
    },
    {
      title: "پیرسینگ زنانه و مردانه",
      products: [
        { imageUrl: goshvareh, name: "پیرسینگ گوش ناکسیگو", link: "https://example.com/product10" },
        { imageUrl: dastband, name: "دستبند زنانه", link: "https://example.com/product11" },
      ],
    },
  ];

  return (
    <div className="table-1">
      <h1>محصولات</h1>
      <table>
        <thead>
          <tr>
            <th>دسته‌بندی</th>
            <th>نام محصول</th>
            <th>تصویر</th>
            <th>عملیات</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category, categoryIndex) =>
            category.products.map((product, productIndex) => (
              <tr key={`${categoryIndex}-${productIndex}`}>
                {productIndex === 0 && (
                  <td rowSpan={category.products.length} className="category">
                    {category.title}
                  </td>
                )}
                <td className="product-name">{product.name}</td>
                <td>
                  {product.imageUrl ? (
                    <a href={product.link} target="_blank" rel="noopener noreferrer">
                      <img
                        src={product.imageUrl}
                        alt={product.name}
                        className="product-image"
                      />
                    </a>
                  ) : (
                    <div className="placeholder">عکس</div>
                  )}
                </td>
                <td>
                  <button>افزودن به سبد</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default TableOne;
