// src/components/ImagePair.js
import React from 'react';
import './ImagePair.css'; // فایل استایل جداگانه

// وارد کردن تصاویر
import image1 from '../../assets/70%offer.gif';
import image2 from '../../assets/20%offer.webp';

const ImagePair = () => {
  return (
    <div className="image-pair-container">
      <div className="image-wrapper">
        <img src={image1} alt="توضیح تصویر 1" className="image" />
      </div>
      <div className="image-wrapper">
        <img src={image2} alt="توضیح تصویر 2" className="image" />
      </div>
    </div>
  );
};

export default ImagePair;
