import React from 'react';
import './PromoBanners.css';

// ایمپورت عکس‌ها

import euroVitalLogo from './assets/euro-vital-logo.png';
import peymanLogo from './assets/peyman-logo.png';
import xVisionLogo from './assets/x-vision-logo.png';
import garinanLogo from './assets/garinan-logo.png';
import huaweiLogo from './assets/huawei-logo.png';
import parsKhazarLogo from './assets/pars-khazar-logo.png';
import parsisLogo from './assets/parsis-logo.png';

const SkincareBanner = () => {
  return (
    <div className="banner skincare-banner">
      <div className="logo">
        <img src={synSkinLogo} alt="SYN SKIN icon" />
        <span>SYN SKIN</span>
      </div>
      <div className="products">
        <img src={skincareProduct1} alt="Product 1" className="product" />
        <img src={skincareProduct2} alt="Product 2" className="product" />
        <img src={skincareProduct3} alt="Product 3" className="product" />
      </div>
      <div className="text">
        <p className="discount">تخفیف 45%</p>
        <p className="summer-products">محصولات تابستانی</p>
        <p className="details">سن سیف و سیف رز تابستانه</p>
        <div className="leaf-icon" />
      </div>
      <button className="buy-button">خرید</button>
    </div>
  );
};

const JellyBanner = () => {
  return (
    <div className="banner jelly-banner">
      <div className="decorative-elements">
        <div className="sun" />
        <div className="wave" />
        <div className="beach-ball" />
      </div>
      <div className="products">
        <img src={jellyProduct1} alt="Jelly Product 1" className="product" />
        <img src={jellyProduct2} alt="Jelly Product 2" className="product" />
        <img src={jellyProduct3} alt="Jelly Product 3" className="product" />
      </div>
      <div className="text">
        <p className="discount">تخفیف 20%</p>
        <p className="summer-products">یه عالمه ژله با طعم سیب، پرتقال، توت فرنگی، سیف رز تابستانه</p>
      </div>
      <button className="buy-button">خرید</button>
    </div>
  );
};

const LogosBanner = () => {
  return (
    <div className="logos-banner">
      <div className="header">
        <span className="star-icon" />
        <span className="header-text">محسن پرینتر شما</span>
      </div>
      <div className="logos-container">
        <img src={euroVitalLogo} alt="Euro Vital" />
        <img src={peymanLogo} alt="Peyman" />
        <img src={xVisionLogo} alt="X-Vision" />
        <img src={garinanLogo} alt="Garinan" />
        <img src={huaweiLogo} alt="Huawei" />
        <img src={parsKhazarLogo} alt="Pars Khazar" />
        <img src={parsisLogo} alt="Parsis" />
      </div>
    </div>
  );
};

const PromoBanners = () => {
  return (
    <div className="promo-banners-container">
      <div className="banners-container">
        <SkincareBanner />
        <JellyBanner />
      </div>
      <LogosBanner />
    </div>
  );
};

export default PromoBanners;
