import React from 'react';
import './Table.css';

// وارد کردن عکس‌ها
// import bishtr from '../../assets/';
import placeholder2 from '../../assets/تخفیف بومی محلی.png';
import placeholder3 from '../../assets/حراج.png';
import placeholder4 from '../../assets/دیجیکالا سرویس.png';
import placeholder5 from '../assets/placeholder5.png';
import placeholder6 from '../assets/placeholder6.png';
import placeholder7 from '../assets/placeholder7.png';
import placeholder8 from '../assets/placeholder8.png';
import placeholder9 from '../assets/placeholder9.png';
import placeholder10 from '../assets/placeholder10.png';

// آرایه‌ای از آیتم‌ها با استفاده از تصاویر وارد شده
const menuItems = [
  // { id: 1, icon: placeholder1, title: 'بیشتر', alt: 'بیشتر' },
  { id: 2, icon: placeholder2, title: 'تخفیف بومی', alt: 'تخفیف بومی' },
  { id: 3, icon: placeholder3, title: 'حراج فروشگاه حضوری', alt: 'حراج فروشگاه حضوری' },
  { id: 4, icon: placeholder4, title: 'دی جی کالا سرویس', alt: 'دی جی کالا سرویس' },
  { id: 5, icon: placeholder5, title: 'اشتراک پلاس', alt: 'اشتراک پلاس' },
  { id: 6, icon: placeholder6, title: 'تخفیف برندها', alt: 'تخفیف برندها' },
  { id: 7, icon: placeholder7, title: '۴قسط', alt: '۴قسط' },
  { id: 8, icon: placeholder8, title: 'پس نده', alt: 'پس نده' },
  { id: 9, icon: placeholder9, title: 'خرید طلای دیجیتال', alt: 'خرید طلای دیجیتال' },
  { id: 10, icon: placeholder10, title: 'حراج موبایل کارکرده با گارانتی', alt: 'حراج موبایل کارکرده با گارانتی' },
];

const Table = () => {
  return (
    <div className="table-container">
      {menuItems.map((item) => (
        <div key={item.id} className="table-item">
          <div className="table-icon">
            <img src={item.icon} alt={item.alt} />
          </div>
          <p>{item.title}</p>
        </div>
      ))}
    </div>
  );
};

export default Table;
