import React from 'react';
import './Table_1.css'; // فایل استایل برای Table_1

const ProductCard = ({ imageUrl, name }) => (
  <div className="product-card">
    {imageUrl ? <img src={imageUrl} alt={name} /> : <div className="placeholder">عکس</div>}
    <p>{name}</p>
    <button>افزودن به سبد</button>
  </div>
);

const CategorySection = ({ title, products }) => (
  <div className="category-section">
    <h2>{title}</h2>
    <div className="product-grid">
      {products.map((product, index) => (
        <ProductCard key={index} imageUrl={product.imageUrl} name={product.name} />
      ))}
    </div>
  </div>
);

const Table_1 = () => {
  const categories = [
    {
      title: "کرم و مرطوب‌کننده",
      products: [
        { imageUrl: null, name: "سویدا" },
        { imageUrl: null, name: "سویدا" },
        { imageUrl: null, name: "ویتامین C" },
        { imageUrl: null, name: "هیدروکرم" },
      ],
    },
    {
      title: "عطر",
      products: [
        { imageUrl: null, name: "عطر گل" },
        { imageUrl: null, name: "M2" },
      ],
    },
    {
      title: "انرژی‌زا و نوشیدنی",
      products: [
        { imageUrl: null, name: "Fire Power" },
        { imageUrl: null, name: "Star" },
        { imageUrl: null, name: "TNT" },
      ],
    },
    {
      title: "اکسسوری و زیورآلات",
      products: [
        { imageUrl: null, name: "گردنبند" },
        { imageUrl: null, name: "دستبند" },
      ],
    },
  ];

  return (
    <div className="table-1">
      {categories.map((category, index) => (
        <CategorySection key={index} title={category.title} products={category.products} />
      ))}
    </div>
  );
};

export default Table_1;
