import './Table.css';
import React from 'react';

// آرایه‌ای از آیتم‌ها. در اینجا از آدرس عکس موقت استفاده شده.
const menuItems = [
  { id: 1, icon: 'placeholder1.png', title: 'بیشتر', alt: 'بیشتر' },
  { id: 2, icon: '../', title: 'تخفیف بومی', alt: 'تخفیف بومی' },
  { id: 3, icon: 'placeholder3.png', title: 'حراج فروشگاه حضوری', alt: 'حراج فروشگاه حضوری' },
  { id: 4, icon: 'placeholder4.png', title: 'دی جی کالا سرویس', alt: 'دی جی کالا سرویس' },
  { id: 5, icon: 'placeholder5.png', title: 'اشتراک پلاس', alt: 'اشتراک پلاس' },
  { id: 6, icon: 'placeholder6.png', title: 'تخفیف برندها', alt: 'تخفیف برندها' },
  { id: 7, icon: 'placeholder7.png', title: '۴قسط', alt: '۴قسط' },
  { id: 8, icon: 'placeholder8.png', title: 'پس نده', alt: 'پس نده' },
  { id: 9, icon: 'placeholder9.png', title: 'خرید طلای دیجیتال', alt: 'خرید طلای دیجیتال' },
  { id: 10, icon: 'placeholder10.png', title: 'حراج موبایل کارکرده با گارانتی', alt: 'حراج موبایل کارکرده با گارانتی' },
];


const Table = () => {
  return (
    <div className="menu-container">
      {menuItems.map((item) => (
        <div key={item.id} className="menu-item">
          <div className="menu-icon">
            {/* وقتی عکس اصلی را داشتی، به جای placeholder از لینک اصلی استفاده کن */}
            <img src={item.icon} alt={item.title} />
          </div>
          <p>{item.title}</p>
        </div>
      ))}
    </div>
  );
};

export default Table;
