import React from "react";
import "./Tailwind.css"; // استایل جداگانه در صورت نیاز

const products = [
  { id: 1, img: "placeholder.jpg", discount: "40%" },
  { id: 2, img: "placeholder.jpg", discount: "35%" },
  { id: 3, img: "placeholder.jpg", discount: "25%" },
  { id: 4, img: "placeholder.jpg", discount: "30%" },
  { id: 5, img: "placeholder.jpg", discount: "50%" },
];

const Tailwind = () => {
  return (
    <div className="promo-wrapper">
      {/* دکمه بیشتر ببینید */}
      <div className="more-btn">
        <span>← بیش از 100 کالا</span>
      </div>

      {/* اسلایدر محصولات */}
      <div className="promo-slider">
        {products.map((product) => (
          <div key={product.id} className="promo-item">
            <img src={product.img} alt={`محصول ${product.id}`} />
            <span className="discount-badge">{product.discount}</span>
          </div>
        ))}
      </div>

      {/* عنوان و آیکون خرید */}
      <div className="promo-title">
        <span>شگفت انگیز سوپرمارکتی</span>
        <button className="promo-button">تخفیف تا 45%</button>
      </div>
    </div>
  );
};

export default Tailwind;
